import { TransformParameter } from '../declarations/util-types'
import { AnyApi } from './Api'
import { AnyResource } from './Resource'
import { ResourceFieldName, ResourceFieldNames } from './ResourceField'

export type AnyApiQueryParameters = ApiQueryParameters<AnyApi, AnyResource>
export type AnyApiSortQuery = ApiSortQuery<ResourceFieldName>
export type AnyApiQuerySortRule = ApiQuerySortRule<ResourceFieldName>

export type ApiQueryKey = string

export type ApiQueryValue =
  | ApiQueryParameterValue
  | Record<ApiQueryKey, ApiQueryParameterValue>

export type ApiQueryParameterKey = string

export type ApiQueryParameters<
  A extends AnyApi,
  R extends AnyResource
> = Query &
  ApiPageQuery<TransformParameter<A['setup']['createPageQuery']>> &
  ApiSortQuery<ResourceFieldNames<R>>

export type Query =
  | { [key: string]: AnyApiSortQuery }
  | { sort: Array<AnyApiQuerySortRule> }

export type ApiQueryParameterValue = string | number | Array<string | number>

export type ApiPageQuery<T> = { page: T }

export type ApiSortQuery<T extends ResourceFieldName> = {
  sort: Array<ApiQuerySortRule<T>>
}

export class ApiQuery<A extends AnyApi, R extends AnyResource> {
  values: Array<any>
  constructor(parameters: Partial<ApiQueryParameters<A, R>>) {
    this.values = Object.entries(parameters)
      .reduce(
        (values: Array<ApiQueryParameter>, [name, value]: [string, any]) => {
          if (value != null) {
            const valueType = typeof value
            if (
              Array.isArray(value) ||
              valueType === 'string' ||
              valueType === 'number'
            ) {
              if (value != null && String(value).length !== 0) {
                values.push(new ApiQueryParameter(name, null, value))
              }
            } else if (valueType === 'object') {
              Object.keys(value).forEach((key) => {
                if (value[key] != null && String(value[key]).length !== 0) {
                  values.push(new ApiQueryParameter(name, key, value[key]))
                }
              })
            }
          }
          return values
        },
        [],
      )
      .sort((a: ApiQueryParameter, b: ApiQueryParameter) => {
        if (a.name !== b.name) {
          return a.name < b.name ? -1 : 1
        }
        if (a.key !== b.name) {
          if (a.key === null) {
            return -1
          } else if (b.key === null) {
            return 1
          }
          return a.key < b.key ? -1 : 1
        }
        return a.value < b.value ? -1 : 1
      })
  }

  toString(): string {
    return this.values.length !== 0
      ? `?${this.values.map(String).join('&')}`
      : ''
  }
}

export class ApiQueryParameter {
  name: ApiQueryKey
  key: string | null
  value: ApiQueryParameterValue

  constructor(
    name: ApiQueryKey,
    key: string | null,
    value: ApiQueryParameterValue,
  ) {
    this.name = name
    this.key = key
    this.value = value
  }

  toString(): string {
    return this.key === null
      ? `${encodeURIComponent(this.name)}=${encodeURIComponent(
          String(this.value),
        )}`
      : `${encodeURIComponent(
          `${this.name}[${this.key}]`,
        )}=${encodeURIComponent(String(this.value))}`
  }
}

export class ApiQuerySortRule<T extends ResourceFieldName> {
  name: T
  ascending: boolean

  constructor(name: T, ascending: boolean) {
    this.name = name
    this.ascending = ascending
  }

  toString(): string {
    return this.ascending !== false ? this.name : `-${name}`
  }

  static ascending<T extends ResourceFieldName>(name: T): ApiQuerySortRule<T> {
    return new ApiQuerySortRule(name, true)
  }

  static descending<T extends ResourceFieldName>(name: T): ApiQuerySortRule<T> {
    return new ApiQuerySortRule(name, false)
  }
}
