import { __DEV__ } from '../utils/constants'
import { createEmptyObject } from '../utils/data'
import { Nullable, WithoutNever } from '../utils/generics'
import { isNonEmptyURIComponent } from '../utils/types'
import {
  AnyResourceField,
  ResourceFieldName,
  ResourceFieldValue,
} from './ResourceField'
import { createResourceFilterQuery } from './ApiController'
import {
  AnyResourceFilterOptions,
  ResourceFilter,
  ResourceFilterOptions,
} from './ResourceFilter'
import { ResourceIdentifier, ResourceIdentifierKey } from './ResourceIdentifier'

export type AnyResource = ResourceIdentifier<ResourceType>

export type AnyResourceConstructor = ResourceConstructor<any>
export type AnyResourceInstance = InstanceType<AnyResourceConstructor>

export type Resource<R extends AnyResource> = DefaultResourceFragment<R>

export type ResourceFragment<
  R extends AnyResource,
  F extends Nullable<AnyResourceFilterOptions>
> = null extends F
  ? DefaultResourceFragment<R>
  : F extends AnyResourceFilterOptions
  ? FilteredResourceFragment<R, { [K in keyof F]: F[K][number] }>
  : never

export type ResourceModel<
  T extends ResourceType,
  M extends Omit<ResourceModel<T, M>, ResourceIdentifierKey>
> = ResourceIdentifier<T> &
  WithoutNever<
    {
      [K in keyof M]: K extends ResourceFieldName
        ? M[K] extends ResourceFieldValue
          ? M[K]
          : never
        : never
    }
  >

export type ResourceConstructor<R extends AnyResource> = {
  type: ResourceType & R['type']
  fields: Record<ResourceFieldName, AnyResourceField>
  new (item: R): R
}

export type ResourceInstance<R extends AnyResource> = InstanceType<
  ResourceConstructor<R>
>

export type ResourceType = string
export type ResourceId = string

export const resource = <T extends ResourceType>(type: T) => {
  if (__DEV__ && !isNonEmptyURIComponent(type)) {
    throw new TypeError(`Invalid type for resource`)
  }
  return class Resource<
    M extends ResourceModel<T, M>
  > extends ResourceIdentifier<T> {
    static type: T = type
    static fields: Record<
      ResourceFieldName,
      AnyResourceField
    > = createEmptyObject()

    constructor(item: M) {
      super(item.type, item.id)
      Object.assign(this, item)
      if (__DEV__) {
        Object.freeze(this)
      }
    }
  }
}

export const resourceFilter = <
  R extends AnyResource,
  F extends ResourceFilterOptions<R>
>(
  Resource: ResourceConstructor<R>,
  fields: F,
): ResourceFilter<R, F> => {
  const query = createResourceFilterQuery(Resource, fields) as any
  return new ResourceFilter(Resource.type, fields, query)
}

// RESOURCE FRAGMENT
type FilteredRelationshipField<T> = T extends AnyResource
  ? Pick<T, ResourceIdentifierKey>
  : T

type FilteredRelationshipFragment<
  M extends AnyResource,
  F extends { [key: string]: string }
> = WithoutNever<
  {
    [K in keyof M]: K extends ResourceIdentifierKey
      ? M[K]
      : K extends F[M['type']]
      ? M[K] extends Array<AnyResource>
        ? Array<FilteredRelationshipField<M[K][any]>>
        : FilteredRelationshipField<M[K]>
      : never
  }
>

type FilteredResourceField<
  T,
  F extends { [key: string]: string }
> = T extends Array<AnyResource>
  ? Array<FilteredRelationshipFragment<T[number], F>>
  : Extract<T, AnyResource> extends never
  ? T
  : Nullable<FilteredRelationshipFragment<Extract<T, AnyResource>, F>>

// todo: check WithoutNever for ResourceFragment regression
export type FilteredResourceFragment<
  M extends AnyResource,
  F extends { [key: string]: string }
> = WithoutNever<
  {
    [K in keyof M]: K extends ResourceIdentifierKey
      ? M[K]
      : K extends F[M['type']]
      ? FilteredResourceField<M[K], F>
      : never
  }
>

// only get resource identifier object for relationships by default
export type DefaultResourceFragment<M extends AnyResource> = {
  [K in keyof M]: Exclude<M[K], null> extends AnyResource
    ? Nullable<Pick<Extract<M[K], AnyResource>, ResourceIdentifierKey>>
    : M[K] extends Array<AnyResource>
    ? Array<Pick<M[K][number], ResourceIdentifierKey>>
    : M[K]
}

