import { Nullable, TypeGuard, WithoutNever } from '../declarations/util-types'
import {
  AnyResource,
  AnyResourceConstructor,
  ResourceId,
  ResourceType,
  resourceRepository,
} from './Resource'
import { SerializableValue } from '../utils/data'
import {
  AnyResourceIdentifier,
  ResourceIdentifier,
  ResourceIdentifierKey,
} from './ResourceIdentifier'

export type AnyResourceField =
  | AnyResourceIdentifierField
  | AnyResourceAttributeField
  | AnyResourceRelationshipField

export type AnyResourceIdentifierField = ResourceIdentifierField<
  ResourceIdentifierKey,
  ResourceId | ResourceType
>

export type AnyResourceAttributeField = ResourceAttributeField<
  ResourceFieldName,
  ResourceAttributeFieldValue
>

export type AnyResourceRelationshipField = ResourceRelationshipField<
  ResourceFieldName,
  AnyResourceRelationship
>

export type ResourceFieldValue =
  | ResourceAttributeFieldValue
  | ResourceRelationshipFieldValue

export type AnyToOneResourceRelationship = ToOneResourceRelationship<
  AnyResource
>

export type AnyToManyResourceRelationship = ToManyResourceRelationship<
  AnyResource
>

export type AnyResourceRelationship =
  | AnyToOneResourceRelationship
  | AnyToManyResourceRelationship

export type ResourceFieldName = string

export type ResourceFieldRoot = Nullable<'attributes' | 'relationships'>

export type ResourceAttributeFieldValue = SerializableValue
export type ResourceRelationshipFieldValue =
  | Nullable<AnyResourceIdentifier>
  | Array<AnyResourceIdentifier>

export type ResourceRelationshipFieldResource<
  R extends AnyResourceRelationship
> = R extends AnyToManyResourceRelationship
  ? R[number]
  : Extract<R, AnyResource>

export type ResourceRelationshipFieldResourceIdentifier<
  R extends AnyResourceRelationship
> = R extends AnyToManyResourceRelationship
  ? ResourceIdentifier<R[number]['type']>
  : ResourceIdentifier<Extract<R, AnyResource>['type']>

export type ToOneResourceRelationship<R extends AnyResource> = Nullable<R>
export type ToManyResourceRelationship<R extends AnyResource> = Array<R>

export type ResourceFieldNames<R extends AnyResource> = Extract<
  Exclude<keyof R, ResourceIdentifierKey>,
  ResourceFieldName
>

export type ResourceAttributes<R extends AnyResource> = WithoutNever<
  {
    [K in keyof R]: K extends ResourceIdentifierKey
      ? never
      : R[K] extends ResourceAttributeFieldValue
      ? R[K]
      : never
  }
>

const resourceFieldDecoratorDescriptor = {
  configurable: false,
  enumerable: true,
  writable: true,
}

export const optionalAttribute = <T extends ResourceAttributeFieldValue>(
  validate: TypeGuard<T>,
): any => <K extends ResourceFieldName>(target: any, name: K) => {
  target.constructor.fields[name] = new ResourceAttributeField(name, validate)
  return resourceFieldDecoratorDescriptor
}

export const attribute = <T extends ResourceAttributeFieldValue>(
  validate: TypeGuard<T>,
): any => <K extends ResourceFieldName>(target: any, name: K) => {
  target.constructor.fields[name] = new ResourceAttributeField(name, validate)
  return resourceFieldDecoratorDescriptor
}

export const relationship = <T extends ResourceType>(
  kind: 'to-one' | 'to-many',
  type: ResourceType,
): any => <K extends ResourceFieldName>(target: any, name: K) => {
  target.constructor.fields[name] = new ResourceRelationshipField(name, type)
  return resourceFieldDecoratorDescriptor
}

relationship.toOne = (type: ResourceType) => relationship('to-one', type)
relationship.toMany = (type: ResourceType) => relationship('to-many', type)

export class ResourceField<
  K extends ResourceFieldName,
  T extends ResourceFieldValue
> {
  name: K
  root: ResourceFieldRoot
  validate: TypeGuard<T>

  constructor(name: K, root: ResourceFieldRoot, validate: TypeGuard<T>) {
    this.name = name
    this.root = root
    this.validate = validate
  }
}

export class ResourceIdentifierField<
  K extends ResourceIdentifierKey,
  T extends ResourceId | ResourceType
> extends ResourceField<K, T> {
  constructor(name: K, validate: TypeGuard<T>) {
    super(name, null, validate)
  }
}

export class ResourceAttributeField<
  K extends ResourceFieldName,
  T extends ResourceAttributeFieldValue
> extends ResourceField<K, T> {
  root: ResourceFieldRoot = 'attributes'
  constructor(name: K, validate: TypeGuard<T>) {
    super(name, 'attributes', validate)
  }
}

export class ResourceRelationshipField<
  K extends ResourceFieldName,
  R extends AnyResourceRelationship
> extends ResourceField<K, R> {
  root: ResourceFieldRoot = 'relationships'
  type: ResourceType

  constructor(name: K, type: ResourceRelationshipFieldResource<R>['type']) {
    super(name, 'relationships', validateResourceIdentifier(type) as any)
    this.type = type
  }
}

const isStringWithLength = (value: any): value is string =>
  typeof value === 'string' && value.length > 0

const validateResourceIdentifier = <T extends ResourceType>(type: T) => (
  value: any,
): value is Nullable<ResourceIdentifier<T>> => {
  const Resource: AnyResourceConstructor = resourceRepository.get(type)!
  return (
    Resource &&
    value &&
    value.type === Resource.type &&
    isStringWithLength(value.id)
  )
}
