import Type from './lib/Type'
import { number, object, string } from './types/index'

export type Description = string
export type ObjectOf<K extends PropertyKey, T> = { [P in K]: T }
export type Static<T extends Type<any>> = InferType<T['check']>
export type TypeGuard<T> = (value: any, ...rest: Array<unknown>) => value is T
export type InferType<T extends TypeGuard<any>> = T extends TypeGuard<infer R>
  ? R
  : never

export const is = <T>(
  description: Description,
  check: TypeGuard<T>,
): Type<T> => {
  return new Type(description, check)
}

export const optional = <T>(type: Type<T>): Type<T | undefined> => {
  return type.or(Type.literal(undefined))
}

export const nullable = <T>(type: Type<T>): Type<T | null> => {
  return type.or(Type.literal(null))
}

export const literal = <T extends string | number | boolean | null | undefined>(
  literalValue: T,
): Type<T> => {
  return Type.is(
    `equal to ${String(literalValue)}`,
    (value: any): value is T => value === literalValue,
  )
}

export const range = (min: number, max: number): Type<number> => {
  return number.and(
    Type.is(
      `between ${min} and ${max}`,
      (value: any): value is number => value >= min && value <= max,
    ),
  )
}

export const at = <K extends PropertyKey, T>(
  key: K,
  type: Type<T>,
): Type<ObjectOf<K, T>> => {
  if (typeof key === 'number') {
    return Type.is(
      `an Array where index ${key} is ${type.description}`,
      (value: any): value is ObjectOf<K, T> =>
        Array.isArray(value) && type.check(value[key]),
    )
  }
  return Type.is(
    `an object where ${String(key)} is ${type.description}`,
    (value: any): value is ObjectOf<K, T> =>
      value != null && type.check(value[key]),
  )
}

export const matches = (
  description: string,
  expression: RegExp,
): Type<string> => {
  return string.and(
    Type.is(
      `a match with ${description}`,
      (value: any): value is string => (
        (expression.lastIndex = 0), expression.test(value)
      ),
    ),
  )
}

export const array = <T>(type: Type<T>): Type<Array<T>> => {
  return Type.is(
    `an array where every element is ${type.description}`,
    (value: any): value is Array<T> =>
      Array.isArray(value) && value.every(type.check),
  )
}

export const record = <T extends Record<string, Type<any>>>(
  rules: T,
): Type<{ [K in keyof T]: Static<T[K]> }> => {
  return Object.keys(rules).reduce(
    (type, key) =>
      type.and(
        Type.is(
          `an object where ${key} is ${rules[key].description}`,
          (value: any): value is {} =>
            value != null && rules[key].check(value[key]),
        ),
      ),
    object,
  ) as any
}

export const tuple = <T extends { [index: number]: any }>(
  types: Array<Type<T[number]>>,
): Type<T> => {
  return Type.is(
    `an Array of ${types.length} elements`,
    (value: any): value is T =>
      Array.isArray(value) &&
      types.every((type, index) => type.check(value[index])),
  )
}

// examples
const point = tuple([number, string])
const email = matches(`an e-mail address`, /@/)
const stringOrNumber = string.or(number)
const stringAndEmail = string.and(email)
const optionalString = optional(string)

const identifier = record({
  id: string,
})

const user = record({
  name: string,
  age: number,
  address: record({
    street: string,
    city: string,
  }),
}).and(identifier)

const optionalUser = optional(user)

const userIdentifier = user.and(identifier)

console.log(user)
