import { Nullable } from '../declarations/util-types'
import { ResourceFieldName } from './ResourceField'

export type ApiErrorMessage = string
export type ApiErrorPointer = Nullable<ResourceFieldName>
export type ApiRequestStatus = Nullable<number>

export class ApiError {
  message: ApiErrorMessage
  pointer: ApiErrorPointer
  status: ApiRequestStatus

  constructor(
    message: ApiErrorMessage,
    pointer: ApiErrorPointer,
    status: ApiRequestStatus,
  ) {
    this.message = message
    this.pointer = pointer
    this.status = status
  }

  toString(): string {
    return this.message
  }

  valueOf(): ApiRequestStatus {
    return this.status
  }
}

export class ApiValidationError extends ApiError {
  constructor(message: ApiErrorMessage, pointer: ResourceFieldName) {
    super(message, pointer, null)
  }
}

export class ApiRequestError extends ApiError {
  constructor(
    message: ApiErrorMessage,
    pointer: ApiErrorPointer,
    status: ApiRequestStatus,
  ) {
    super(message, pointer, status)
  }
}
