import { __DEV__ } from '../utils/constants'
import { createEmptyObject } from '../utils/data'
import { FieldName, Model, ResourceType } from '../utils/generics'
import { isNonEmptyURIComponent } from '../utils/types'
import { ResourceIdentifier } from './ResourceIdentifier'
import { ResourceField } from './ResourceField'

export const resource = <T extends ResourceType>(type: T) => {
  if (__DEV__) {
    isNonEmptyURIComponent.assert(type, 'Api resource type')
  }
  return class Resource<M extends Model<T, M>> extends ResourceIdentifier<T> {
    static type: T = type
    static Identifier = ResourceIdentifier
    static fields: Record<FieldName, ResourceField> = createEmptyObject()

    constructor(item: M) {
      super(item.type, item.id)
      Object.assign(this, item)
    }
  }
}
