import {
  __DEV__,
  JsonApiRoot,
  RelationshipMode,
  defaultGetRequestHeaders,
  HTTPMethod,
} from '../utils/constants'
import { createDataObject, createBaseResourceQuery } from '../utils/data'
import {
  AnyResourceConstructor,
  AnyResourceFilter,
  AnyResourceFilterFields,
  ApiEventListener,
  ApiEventOrigin,
  ApiURL,
  JsonApiSuccesResponse,
  JsonApiIncludedData,
  JsonApiResourceData,
  ResourceId,
  ResourceType,
  FieldName,
} from '../utils/generics'
import {
  isArray,
  isFunction,
  isResourceIdentifier,
  isString,
  isWithData,
  isWithAttributes,
  isWithRelationships,
  isNonEmptyArray,
} from '../utils/types'
import { Api } from './Api'
import { ApiEvent } from './ApiEvent'
import { ApiQuery } from './ApiQuery'
import { ResourceField, RelationshipField } from './ResourceField'

const emptyApiQuery = new ApiQuery({})
const controllers: Record<ApiURL, ApiController> = createDataObject(null)

export class ApiController {
  api: Api
  listeners: Array<ApiEventListener> = []
  resources: Record<ResourceType, AnyResourceConstructor> = createDataObject(
    null,
  )

  constructor(api: Api) {
    this.api = api
  }

  createResourceFilterQuery(
    Resource: AnyResourceConstructor,
    fields: AnyResourceFilterFields,
  ): ApiQuery {
    // todo: throw error if a type that is not included is queried
    if (fields === null) {
      return emptyApiQuery
    }
    const fieldEntries = Object.entries(fields)
    if (fieldEntries.length === 0) {
      return emptyApiQuery
    }
    const resourceFieldNames = Object.keys(Resource.fields)
    const query = Object.entries(fields).reduce(
      (resourceQuery, [type, values]) => {
        isNonEmptyArray.assert(values)
        if (type !== Resource.type) {
          // find relationship fields that match filter type
          resourceFieldNames
            .filter(
              (name: FieldName) =>
                (Resource.fields[name] as ResourceField).root ===
                  JsonApiRoot.RELATIONSHIPS &&
                (Resource.fields[name] as RelationshipField).type === type,
            )
            .forEach((name: FieldName) => {
              const encodedFieldName = this.api.encodeFieldName(name)
              if (!resourceQuery.include.includes(encodedFieldName)) {
                resourceQuery.include.push(encodedFieldName)
              }
            })
        }
        resourceQuery.fields[type] = values.map((name: FieldName) =>
          this.api.encodeFieldName(name),
        )
        return resourceQuery
      },
      createBaseResourceQuery(),
    )
    return new ApiQuery(query)
  }

  decodeApiResponseData(
    Resource: AnyResourceConstructor,
    data: JsonApiResourceData,
    included: JsonApiIncludedData,
    fields: Exclude<AnyResourceFilterFields, null>,
  ) {
    isResourceIdentifier.assert(data, 'Resource')
    const resourceFields = fields[Resource.type] || Object.keys(Resource.fields)
    // console.log(Resource.type, 'fields', resourceFields)
    const item = resourceFields.reduce(
      (item: any, name: FieldName) => {
        const field = Resource.fields[name] as any // todo: any -> AnyResourceField
        const value = this.getResponseDataValue(data, field)
        // console.log(Resource.type, name, value)
        switch (field.root) {
          case null: {
            item[field.name] = value
            return item
          }
          case JsonApiRoot.ATTRIBUTES: {
            if (field.validate(value)) {
              item[field.name] = value
            } else {
              throw field.description
            }
            return item
          }
          case JsonApiRoot.RELATIONSHIPS: {
            if (field.mode === RelationshipMode.TO_ONE) {
              item[field.name] =
                value === null
                  ? null
                  : this.decodeApiResponseRelationshipData(
                      field,
                      value,
                      included,
                      fields,
                    )
            } else {
              isArray.assert(value, 'Relationship data')
              item[field.name] = value.map((value: JsonApiResourceData) =>
                this.decodeApiResponseRelationshipData(
                  field,
                  value,
                  included,
                  fields,
                ),
              )
            }
            return item
          }
        }
      },
      createDataObject({
        type: data.type,
        id: data.id,
      }),
    )
    return new Resource(item)
  }

  decodeApiResponseRelationshipData(
    field: RelationshipField,
    data: JsonApiResourceData,
    included: JsonApiIncludedData,
    fields: Exclude<AnyResourceFilterFields, null>,
  ) {
    isResourceIdentifier.assert(data)
    const Resource = this.getApiResource(field.type)!
    if (field.type in fields) {
      const relationshipData = included.find(
        (item) => item.id === data.id && item.type === data.type,
      )!

      return this.decodeApiResponseData(
        Resource,
        relationshipData,
        included,
        fields,
      )
    }
    return new Resource.Identifier(data.type, data.id)
  }

  getResponseDataValue(data: any, field: any) {
    const name = this.api.encodeFieldName(field.name)
    switch (field.root) {
      case null: {
        return isString.assert(data[name])
      }
      case JsonApiRoot.ATTRIBUTES: {
        isWithAttributes.assert(data, 'Resource data')
        return data[field.root][name]
      }
      case JsonApiRoot.RELATIONSHIPS: {
        isWithRelationships.assert(data, 'Resource data')
        const relationshipValue = data[field.root][name]
        isWithData.assert(relationshipValue)
        return relationshipValue[JsonApiRoot.DATA]
      }
    }
  }

  async fetchGetRequest<T>(url: string): Promise<JsonApiSuccesResponse<T>> {
    const response = await fetch(url, {
      method: HTTPMethod.GET,
      headers: defaultGetRequestHeaders,
    }).then((response) => response.json())
    if (JsonApiRoot.ERRORS in response) {
      throw response[JsonApiRoot.ERRORS]
    }
    return response
  }

  async getResourceById(
    Resource: AnyResourceConstructor,
    path: string,
    id: ResourceId,
    filter: AnyResourceFilter,
  ) {
    const { fields, query } = filter
    const { data, included } = await this.fetchGetRequest<JsonApiResourceData>(
      `${this.api.url}/${path}/${id}${String(query)}`,
    )
    return this.decodeApiResponseData(
      Resource,
      data,
      included || [],
      fields || {},
    )
  }

  async getResourceCollection(
    Resource: AnyResourceConstructor,
    path: string,
    filter: AnyResourceFilter,
  ) {
    const { fields, query } = filter
    const { data, included } = await this.fetchGetRequest<
      Array<JsonApiResourceData>
    >(`${this.api.url}/${path}${String(query)}`)
    return data.map((value) =>
      this.decodeApiResponseData(Resource, value, included || [], fields || {}),
    )
  }

  addEventListener(listener: ApiEventListener) {
    if (__DEV__) {
      isFunction.assert(listener, 'ApiEvent listener')
    }
    let currentListener: ApiEventListener | undefined
    if (!this.listeners.includes(listener)) {
      this.listeners.push(listener)
      currentListener = listener
    }
    return () => {
      if (currentListener !== undefined) {
        this.listeners = this.listeners.filter(
          (listener) => listener !== currentListener,
        )
        currentListener = undefined
      }
    }
  }

  dispatchEvent(event: ApiEvent<any>, origin: ApiEventOrigin) {
    this.listeners.forEach((listener) => {
      listener(event, origin)
    })
  }

  addApiResource(Resource: AnyResourceConstructor): this {
    this.resources[Resource.type] = Resource
    return this
  }

  getApiResource(type: ResourceType): AnyResourceConstructor | undefined {
    return this.resources[type]
  }

  static add(api: Api): void {
    if (__DEV__ && api.url in controllers) {
      throw `Api with url "${api.url}" already exists`
    }
    controllers[api.url] = new ApiController(api)
  }

  static get(api: Api): ApiController {
    return controllers[api.url]
  }
}
