import { isArray, isObject, isSerializableNumber, isTrue, isNone } from 'isntnt'
import { __DEV__ } from '../utils/constants'
import { Nullable } from '../utils/generics'
import { isNonEmptyString } from '../utils/types'
import { AnyResource } from './Resource'
import { ResourceAttributeFieldNames } from './ResourceField'

export type AnyApiQuery = ApiQuery<any>

export type ApiQueryValue =
  | ApiQueryParameterValue
  | {
      [key: string]: ApiQueryParameterValue
    }

export type ApiQueryParameterName = string
export type ApiQueryParameterValue =
  | string
  | number
  | boolean
  | Array<string | number>

export class ApiQuery<R extends AnyResource> {
  [Symbol.species] = ApiQuery
  value: Array<ApiQueryParameter>

  constructor(query: ApiResourceQuery<R>) {
    const parameters: Array<ApiQueryParameter> = []
    Object.keys(query).forEach((name) => {
      const value = query[name]
      if (isArray(value)) {
        parameters.push(
          new ApiQueryParameter(name, null, encodeQueryParameterValue(value)),
        )
      } else if (isObject(value)) {
        Object.keys(value).forEach((key) => {
          if (isTrue(value[key])) {
            parameters.push(new ApiQueryParameter(name, key, null))
          } else {
            const encodedValue = encodeQueryParameterValue(value[key])
            if (
              isNonEmptyString(encodedValue) ||
              isSerializableNumber(encodedValue)
            ) {
              parameters.push(new ApiQueryParameter(name, key, encodedValue))
            }
          }
        })
      } else if (isTrue(value)) {
        parameters.push(new ApiQueryParameter(name, null, null))
      } else {
        const encodedValue = encodeQueryParameterValue(value)
        if (
          isNonEmptyString(encodedValue) ||
          isSerializableNumber(encodedValue)
        ) {
          parameters.push(new ApiQueryParameter(name, null, encodedValue))
        }
      }
    })
    this.value = parameters.sort(ApiQueryParameter.sort)
  }

  concat(query: ApiQuery<R> | ApiResourceQuery<R>): ApiQuery<R> {
    // todo: sort concatenated query values
    // todo: merge query values (duplicate parameter names)
    if (ApiQuery.isApiQuery(query)) {
      const result = new ApiQuery({})
      result.value = this.value.concat(query.value).sort(ApiQueryParameter.sort)
      return result
    }
    return this.concat(new ApiQuery(query))
  }

  toString(): string {
    const parameters = this.value.join('&')
    return parameters.length > 0 ? `?${parameters}` : ''
  }

  static isApiQuery(value: any): value is ApiQuery<any> {
    return value != null && value[Symbol.species] === ApiQuery
  }
}

class ApiQueryParameter {
  name: ApiQueryParameterName
  key: Nullable<string>
  value: string | null

  constructor(
    name: string,
    key: Nullable<string> = null,
    value: Nullable<string> = null,
  ) {
    this.name = name
    this.key = key
    this.value = value
  }

  toString(): string {
    return (this.key === null
      ? [this.name, this.value || '']
      : [`${this.name}[${this.key}]`, this.value || '']
    ).join(this.value == null ? '' : '=')
  }

  static sort(a: ApiQueryParameter, b: ApiQueryParameter): 1 | 0 | -1 {
    if (a.name !== b.name) {
      return a.name < b.name ? -1 : 1
    }
    if (a.key !== b.key) {
      return (a.key as string) < (b.key as string) ? -1 : 1
    }
    if (a.value === b.value) {
      return 0
    }
    return (a.value || '') < (b.value || '') ? -1 : 1
  }
}

export class ApiQuerySortOrder<R extends AnyResource> {
  name: ResourceAttributeFieldNames<R>
  ascending: boolean

  constructor(name: ResourceAttributeFieldNames<R>, ascending: boolean) {
    this.name = name
    this.ascending = ascending
  }

  toString() {
    return this.ascending ? this.name : `-${this.name}`
  }
}

export const defaultApiQuery = new ApiQuery({})

const encodeQueryParameterValue = (value: ApiQueryParameterValue): string =>
  Array.isArray(value)
    ? value
        .map(encodeURIComponent)
        .filter(Boolean)
        .sort()
        .join(',')
    : encodeURIComponent(String(value))

export type ApiResourceQuery<R extends AnyResource> = Partial<{
  page: ApiQueryValue
  sort: Array<ApiQuerySortOrder<R>>
  filter: ApiQueryValue
}> & {
  [key: string]: ApiQueryValue
} & Partial<{
    include: never
    fields: never
  }>
